REPLACE INTO Language_en_US (Tag, Text, Gender, Plurality) VALUES ('TXT_KEY_POLICY_BRANCH_EXPLORATION_HELP', 'Abilities:[NEWLINE][NEWLINE][COLOR_POSITIVE_TEXT]Opener[ENDCOLOR][NEWLINE][ICON_MOVES] Moves: +2 for Embarked Units[NEWLINE][ICON_MOVES] Moves: +2 for Great Admirals[NEWLINE][ICON_SIGHT_SEA] Sight: +1 for Military Ships[NEWLINE][ICON_WORKER] Unlocks: Red Fort[NEWLINE][NEWLINE][COLOR_POSITIVE_TEXT]Finisher[ENDCOLOR] [COLOR_GREY](finish all policies in this tree)[ENDCOLOR][NEWLINE][ICON_HAPPINESS_1] +2 National Happiness per Luxury Resource.[NEWLINE][ICON_TEAM_7] Allows the purchase of Great Admirals with faith upon entering the Industrial Era.', '', '');
